import React from 'react';
import { useHistory } from "react-router-dom";
import BookList from '../../components/BookList/BookList';
import './Checkout.css';

const element = new Map()
element.set(0,
  {
    btnConfirm: { className: 'btn btn-primary confirmCheckout' },
    keys: ['cart.donate', 'cart.request']
  }
)

function Checkout({mode}) {

  const history = useHistory()

  element.get(mode).keys.
    some(v => { return (sessionStorage.getItem(v) == null) }) ?
    element.get(mode).btnConfirm.className = 'hide' : null

  function onClickListener(e) {
    if (e.target.className === 'btn btn-primary confirmCheckout')
      history.push('/shipping');
  }

  return (
    <div>
      <section className="container container-margin">
        <div className="text-center">
          <h1 className="text-center">Checkout your cart</h1>
          <button className={element.get(mode).btnConfirm.className} onClick={onClickListener}>Confirm</button>
          <BookList mode={2} />
          <BookList mode={3} />
        </div>
      </section>
    </div>
  );
}

export default Checkout;
